#!/bin/bash
nohup java -Dspring.config.location=application.yml -Xmx500m -XX:+UseG1GC -verbose:gc -Xloggc:/usr/local/log/macos.gc.log -XX:ErrorFile=/usr/local/log/macos.error_%p.log -XX:+PrintGCDetails -XX:+PrintGCDateStamps -XX:+PrintPromotionFailure -XX:+PrintGCApplicationStoppedTime -XX:+PrintHeapAtGC -jar macos-broker-0.0.1.release.jar >/dev/null 2>&1 &